options.cartocss:
/** simple visualization */

#bkgpolygon{
  polygon-fill: #e0e7f2;
  polygon-opacity: 1;
  line-width: 1;
  line-color: #FFF;
  line-opacity: 1;
}

/** simple visualization */

#ne_10m_bathymetry_l_0{
  polygon-fill: #e0e7f2;
  polygon-opacity: 1;
  line-width: 0;
  line-color: #FFF;
  line-opacity: 1;
}

/** simple visualization */

#ne_10m_bathymetry_j_1000{
  polygon-fill: #f1f4f8;
  polygon-opacity: 0.25;
  line-width: 0;
  line-color: #FFF;
  line-opacity: 1;
  polygon-comp-op: multiply;
}

/** simple visualization */

#ne_10m_bathymetry_h_3000{
  polygon-fill: #f1f4f8;
  polygon-opacity: 0.25;
  line-width: 0;
  line-color: #FFF;
  line-opacity: 1;
  polygon-comp-op: multiply;
}

#ne_10m_admin_0_countries_lakes{
  polygon-fill: #f0f0f0;
  polygon-opacity: 1;
  line-width: 0.7;
  line-color: #ABABAB;
  line-opacity: 1;

  [zoom >=1]{
    line-width: 0.4;  	
  }

  [zoom >= 4]{
	::glow{
	  line-width: 2.5;
	  line-color:#ffffff;
      ::line{
        line-color: #ABABAB;
	    line-width: 1;
  	  }
    }
  }

  [zoom >= 6]{
    ::glow{
	  line-width: 3.2;
	  line-color:#ffffff;
      ::line{
        line-color: #ABABAB;
	    line-width: 1.6;
  	  }
    }
  }

  [zoom >= 7]{
    ::glow{
	  line-width: 4;
	  line-color:#ffffff;
      ::line{
        line-color: #ABABAB;
	    line-width: 2;
  	  }
    }
  }

  [zoom >= 8]{
    ::glow{
	  line-width: 4.5;
	  line-color:#ffffff;
      ::line{
        line-color: #ABABAB;
	    line-width: 2;
  	  }
    }
  }
}

#ne_10m_admin_0_countries_lakes::labels{
  [zoom <= 5]{
    text-name: [name];
    text-face-name: 'DejaVu Sans Book';
    text-size: 10;
    text-fill: #888;
    text-allow-overlap: false;
    text-halo-fill: #FFF;
    text-halo-radius: 1.3;
    text-min-path-length: 26;
    [zoom = 5]{
  	  text-size: 12;    
    }
  }
}

/** simple visualization */

#ne_10m_urban_areas{
  [zoom >= 5]{
    line-width: 0;
    line-color: #FFF;
    line-opacity: 1;
    polygon-opacity: 0;
    polygon-comp-op: multiply;
    polygon-pattern-file: url(https://s3.amazonaws.com/com.cartodb.users-assets.production/production/basemaps01/assets/20131016101601Unknown.png);
    polygon-pattern-opacity: 0.3;
  }
}

/** simple visualization */

#ne_10m_rivers_lake_centerlines{
  [zoom >= 7]{
    ::glow{
      line-width: .8;
      line-color: #fff;
      line-opacity: 1;  
    }
    ::line{
      line-width: 0.7;
      line-color: #DAE3EF;
      line-opacity: 1;      
    }
  }
  [zoom >= 8]{
    ::glow{
      line-width: 3;
      line-color: #fff;
      line-opacity: 1;  
    }
    ::line{
      line-width: 2;
      line-color: #DAE3EF;
      line-opacity: 1;      
    }
  }

}

/** simple visualization */

#ne_10m_lakes{
  polygon-fill: #DAE3EF;
  line-color: #fff;
  line-opacity: 0;
  polygon-opacity: 0;  
  [zoom >= 7]{
    line-width: .3;
    line-opacity: 1;
    polygon-opacity: 1;
  }
  [zoom >= 8]{
    line-width: .5;
  }
}

/** simple visualization */

#ne_10m_roads::roads{
  [zoom >= 6]{
    [type = 'Major Highway']{
      line-width: .8;
      line-color: #E1E1E1;
    }
  }
  [zoom >= 7]{
    [type = 'Major Highway']{
      line-width: 1.2;
      line-color: #E1E1E1;
    }
    [type = 'Secondary Highway']{
      line-width: 0.6;
      line-color: #E1E1E1;
    }
  }
  [zoom >= 8]{
    [type = 'Major Highway']{
      ::glow{
        line-width: 3.6;
        line-color: #D7D7D7;
      }
      ::line{
        line-width: 1.8;
        line-color: #fff; 
      }
    }
    [type = 'Secondary Highway']{
      ::glow{
        line-width: 2.8;
        line-color: #D7D7D7;
      }
      ::line{
        line-width: 1.4;
        line-color: #fff; 
      }
    }
  }
  [zoom >= 10]{
    [type = 'Major Highway']{
      ::glow{
        line-width: 4;
        line-color: #D7D7D7;
      }
      ::line{
        line-width: 2.4;
        line-color: #fff; 
      }
    }
    [type = 'Secondary Highway']{
      ::glow{
        line-width: 3.4;
        line-color: #D7D7D7;
      }
      ::line{
        line-width: 1.8;
        line-color: #fff; 
      }
    }
  }
}
#ne_10m_roads::ferry{
  [zoom >= 7]{
    [type = 'Ferry Route']{
      line-width: 1;
      line-color: #ABABAB;
      line-dasharray: 5,5;
      line-opacity: 0.4;
    } 
  }
}

#ne_10m_populated_places_simple::labels{
  [zoom >= 6]{
    text-name: [name];
    text-face-name: 'DejaVu Sans Book';
    text-size: 12;
    text-fill: #777;
    text-allow-overlap: true;
    text-halo-fill: #FFF;
    text-halo-radius: 1.3;
    [scalerank > 2]{
      text-opacity: 0;
    }
  }
  [zoom >= 7]{
    text-allow-overlap: false;
    text-min-distance: 20;  
    text-halo-radius: 1.5;
    [scalerank > 2]{
      text-opacity: 1;
      text-size: 10;
    }
  }
}
