var createGeometry = require('../../../../javascripts/cartodb3/value-objects/geometry');

describe('value-objects/geometry', function () {
  beforeEach(function () {
    this.rawWKBPoint = '0101000020110F00004E3CE77C32B324418662B3BD88715841';
    this.rawWKBPolygon = '';
    this.rawWKTLineString = 'LINESTRING';
  });

  describe('.getRawGeometry', function () {
    it('should return the raw value when geometry is created', function () {
      expect(createGeometry(this.rawWKBPoint).getRawGeometry()).toEqual(this.rawWKBPoint);
      expect(createGeometry(this.rawWKBPolygon).getRawGeometry()).toEqual(this.rawWKBPolygon);
      expect(createGeometry(this.rawWKTLineString).getRawGeometry()).toEqual(this.rawWKTLineString);
    });
  });

  describe('.getPostgresType', function () {
    it('should return the type in Postgres format', function () {
      expect(createGeometry(this.rawWKBPoint).getPostgresType()).toEqual('ST_Point');
      expect(createGeometry(this.rawWKBPolygon).getPostgresType()).toEqual('ST_MultiPolygon');
      expect(createGeometry(this.rawWKTLineString).getPostgresType()).toEqual('ST_LineString');
    });
  });

  describe('.getSimpleType', function () {
    it('should return the type in a simpler format', function () {
      expect(createGeometry(this.rawWKBPoint).getSimpleType()).toEqual('point');
      expect(createGeometry(this.rawWKBPolygon).getSimpleType()).toEqual('polygon');
      expect(createGeometry(this.rawWKTLineString).getSimpleType()).toEqual('line');
    });
  });
});
